import React, { useEffect, useState } from 'react';
import axios from 'axios';

function UserDashboard() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({}); // { [question_id]: 'A' }
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [loading, setLoading] = useState(true);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    axios.get('http://localhost:3000/quiz')
      .then(res => {
        setQuestions(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('❌ Error fetching quiz:', err);
        setLoading(false);
      });
  }, []);

  const handleOptionChange = (questionId, selectedOption) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: selectedOption,
    }));
  };

  const handleSubmit = async () => {
    let calculatedScore = 0;

    // Compare answers with correct ones
    questions.forEach((q) => {
      const selected = answers[q.id];
      if (selected && selected === q.correct_option) {
        calculatedScore += 1;
      }
    });

    setScore(calculatedScore);
    setSubmitted(true);

    try {
      // Submit score to backend
      await axios.post('http://localhost:3000/submit-score', {
        user_id: userId,
        score: calculatedScore,
      });
      console.log('✅ Score submitted to DB:', calculatedScore);
    } catch (err) {
      console.error('❌ Failed to submit score:', err);
    }
  };

  if (loading) {
    return <p>Loading quiz questions...</p>;
  }

  if (submitted) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>Thank you for submitting the quiz!</h2>
        <p>Your final score: <strong>{score}</strong></p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Quiz</h2>

      <form>
        {questions.map((q, index) => (
          <div key={q.id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
            <h3>Q{index + 1}: {q.question}</h3>
            <div>
              {['A', 'B', 'C', 'D'].map(opt => (
                <label key={opt} style={{ display: 'block', margin: '5px 0' }}>
                  <input
                    type="radio"
                    name={`question-${q.id}`}
                    value={opt}
                    checked={answers[q.id] === opt}
                    onChange={() => handleOptionChange(q.id, opt)}
                  />
                  {opt === 'A' && ` ${q.option_a}`}
                  {opt === 'B' && ` ${q.option_b}`}
                  {opt === 'C' && ` ${q.option_c}`}
                  {opt === 'D' && ` ${q.option_d}`}
                </label>
              ))}
            </div>
          </div>
        ))}
      </form>

      <button
        onClick={handleSubmit}
        style={{
          marginTop: '20px',
          fontSize: '18px',
          padding: '10px 20px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer'
        }}
      >
        Submit Quiz
      </button>
    </div>
  );
}

export default UserDashboard;
