import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function UserLogin({ onLogin }) {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3000/user-login', {
        userId,
        password,
      });

      setMessage(response.data.message);
      setError('');

      // Save token and notify parent
      localStorage.setItem('token', response.data.token);
      onLogin(userId); // Notify App of successful login

      // Redirect to user dashboard
      navigate('/quiz');
    } catch (err) {
      if (err.response && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError('❌ Server error or wrong credentials');
      }
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>User Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {message && <p style={{ color: 'green' }}>{message}</p>}
      <div>
        <input
          type="text"
          id="userId" // Added id for accessibility
          name="userId" // Added name for form field identification
          placeholder="User ID"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          style={{ padding: '10px', margin: '5px', width: '200px' }}
        />
      </div>
      <div>
        <input
          type="password"
          id="password" // Added id for accessibility
          name="password" // Added name for form field identification
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: '10px', margin: '5px', width: '200px' }}
        />
      </div>
      <button
        onClick={handleLogin}
        style={{ padding: '10px 20px', marginTop: '10px', cursor: 'pointer' }}
      >
        Login & Start Quiz
      </button>
    </div>
  );
}

export default UserLogin;
