import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LandingPage from './pages/LandingPage';
import AdminLogin from './components/AdminLogin';
import UserLogin from './components/UserLogin';
import AdminPage from './pages/AdminPage';
import UserPage from './pages/UserPage';

function App() {
  const [adminToken, setAdminToken] = useState(localStorage.getItem('adminToken') || '');
  const [userToken, setUserToken] = useState(localStorage.getItem('userToken') || '');

  const handleAdminLogin = (token) => {
    localStorage.setItem('adminToken', token);
    setAdminToken(token);
  };

  const handleUserLogin = (token) => {
    localStorage.setItem('userToken', token);
    setUserToken(token);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('userToken');
    setAdminToken('');
    setUserToken('');
  };

  useEffect(() => {
    const timer = setTimeout(() => handleLogout(), 60 * 60 * 1000); // auto logout in 1 hour
    return () => clearTimeout(timer);
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/admin-login" element={<AdminLogin onLoginSuccess={handleAdminLogin} />} />
        <Route path="/user-login" element={<UserLogin onLoginSuccess={handleUserLogin} />} />
        <Route
          path="/admin"
          element={adminToken ? <AdminPage token={adminToken} /> : <Navigate to="/admin-login" />}
        />
        <Route
          path="/quiz"
          element={userToken ? <UserPage token={userToken} /> : <Navigate to="/user-login" />}
        />
      </Routes>
    </Router>
  );
}

export default App;
