import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AdminLogin() {
  const [adminID, setAdminID] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // Dummy logic, replace with real API call
    if (adminID === 'admin' && password === 'admin123') {
      localStorage.setItem('adminToken', 'validToken');
      navigate('/admin');
    } else {
      setError('‚ùå Invalid credentials');
    }
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        background: '#f0f2f5',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        padding: '20px',
      }}
    >
      <div
        style={{
          background: 'white',
          padding: '40px',
          borderRadius: '12px',
          boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
          maxWidth: '400px',
          width: '100%',
          textAlign: 'center',
        }}
      >
        <h2
          style={{
            fontSize: '28px',
            fontWeight: '700',
            color: '#333',
            marginBottom: '24px',
          }}
        >
          üîê Admin Login
        </h2>

        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Enter Admin ID"
            value={adminID}
            onChange={(e) => setAdminID(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '12px 15px',
              marginBottom: '20px',
              borderRadius: '8px',
              border: '1.5px solid #ccc',
              fontSize: '16px',
              outline: 'none',
              boxSizing: 'border-box',
              transition: 'border-color 0.3s',
            }}
            onFocus={(e) => (e.target.style.borderColor = '#24ec2b')}
            onBlur={(e) => (e.target.style.borderColor = '#ccc')}
          />

          <input
            type="password"
            placeholder="Enter Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '12px 15px',
              marginBottom: '30px',
              borderRadius: '8px',
              border: '1.5px solid #ccc',
              fontSize: '16px',
              outline: 'none',
              boxSizing: 'border-box',
              transition: 'border-color 0.3s',
            }}
            onFocus={(e) => (e.target.style.borderColor = '#24ec2b')}
            onBlur={(e) => (e.target.style.borderColor = '#ccc')}
          />

          <button
            type="submit"
            style={{
              width: '100%',
              padding: '14px',
              backgroundColor: 'rgb(36, 236, 43)',
              color: 'white',
              fontSize: '18px',
              fontWeight: '600',
              border: 'none',
              borderRadius: '30px',
              cursor: 'pointer',
              boxShadow: '0 6px 12px rgba(69, 197, 73, 0.4)',
              transition: 'background-color 0.3s ease',
            }}
            onMouseEnter={e => (e.target.style.backgroundColor = 'rgb(88, 225, 95)')}
            onMouseLeave={e => (e.target.style.backgroundColor = 'rgb(36, 236, 43)')}
          >
             Login
          </button>

          {error && (
            <p style={{ color: 'red', marginTop: '20px', fontWeight: '600' }}>
              {error}
            </p>
          )}
        </form>
      </div>
    </div>
  );
}

export default AdminLogin;
