import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserLogin = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3000/user-login', {
        userId,
        password,
      });

      if (response.data && response.data.user) {
        localStorage.setItem('userId', response.data.user.user_id);
        navigate('/quiz');
      } else {
        setError('Login failed. Please try again.');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Invalid user ID or password.');
    }
  };

  return (
    <div
      style={{
        maxWidth: '400px',
        margin: '100px auto',
        padding: '40px',
        backgroundColor: '#ffffff',
        boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
        borderRadius: '16px',
        textAlign: 'center',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <h2
        style={{
          fontSize: '26px',
          fontWeight: 'bold',
          marginBottom: '24px',
          color: '#2d3748',
        }}
      >
        ðŸ‘¤ User Login
      </h2>

      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Enter User ID"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          required
          style={{
            width: '100%',
            padding: '12px',
            marginBottom: '16px',
            border: '1px solid #ccc',
            borderRadius: '8px',
            fontSize: '16px',
            outline: 'none',
          }}
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{
            width: '100%',
            padding: '12px',
            marginBottom: '20px',
            border: '1px solid #ccc',
            borderRadius: '8px',
            fontSize: '16px',
            outline: 'none',
          }}
        />
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '14px',
            backgroundColor: 'rgb(36, 236, 43)',
            color: '#fff',
            border: 'none',
            borderRadius: '30px',
            fontSize: '18px',
            fontWeight: '600',
            cursor: 'pointer',
            boxShadow: '0 6px 12px rgba(69, 197, 73, 0.4)',
            transition: 'background-color 0.3s ease',
          }}
          onMouseOver={(e) =>
            (e.target.style.backgroundColor = 'rgb(79, 190, 84)')
          }
          onMouseOut={(e) =>
            (e.target.style.backgroundColor = 'rgb(38, 195, 43)')
          }
        >
           Login
        </button>
        {error && (
          <p style={{ color: 'red', marginTop: '16px', fontWeight: '500' }}>
            {error}
          </p>
        )}
      </form>
    </div>
  );
};

export default UserLogin;
