import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPage({ token }) {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [formData, setFormData] = useState({
    type: '',
    question: '',
    option_a: '',
    option_b: '',
    option_c: '',
    option_d: '',
    correct_answer: '',
    timer: ''
  });
  const [questionIdToDelete, setQuestionIdToDelete] = useState('');
  const [commonUserPassword, setCommonUserPassword] = useState('');
  const [message, setMessage] = useState('');

  const backendUrl = 'http://localhost:3000';

  const fetchQuestions = async () => {
    try {
      const res = await axios.get(`${backendUrl}/quiz`);
      setQuestions(res.data);
    } catch (err) {
      console.error('Error fetching questions:', err);
    }
  };

  const fetchScores = async () => {
    try {
      const res = await axios.get(`${backendUrl}/scores`);
      setScores(res.data);
    } catch (err) {
      console.error('Error fetching scores:', err);
    }
  };

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  const handleAddQuestion = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${backendUrl}/add-question`, formData);
      setMessage(res.data);
      setFormData({
        type: '', question: '', option_a: '', option_b: '',
        option_c: '', option_d: '', correct_answer: '', timer: ''
      });
      fetchQuestions();
    } catch (err) {
      console.error('Error adding question:', err);
      setMessage('Failed to add question');
    }
  };

  const handleDeleteQuestion = async () => {
    if (!questionIdToDelete) return;
    try {
      const res = await axios.delete(`${backendUrl}/delete-quiz/${questionIdToDelete}`);
      setMessage(res.data);
      setQuestionIdToDelete('');
      fetchQuestions();
    } catch (err) {
      console.error('Error deleting question:', err);
      setMessage('Failed to delete question');
    }
  };

  const handleSetUserPassword = async () => {
    try {
      const res = await axios.post(`${backendUrl}/set-user-password`, { password: commonUserPassword });
      setMessage(res.data);
    } catch (err) {
      console.error('Error setting user password:', err);
      setMessage('Failed to set password');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-semibold text-center text-blue-600 mb-6">Admin Panel</h2>
      {message && <p className="text-green-600 text-center font-medium mb-4">{message}</p>}

      <div className="bg-white shadow-lg rounded-lg p-6 space-y-6">
        {/* Navbar */}
        <div className="flex justify-center space-x-6 mb-6">
          <button className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600">Add Question</button>
          <button className="bg-red-500 text-white px-6 py-2 rounded-md hover:bg-red-600">Delete Question</button>
          <button className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600">User Scores</button>
          <button className="bg-yellow-500 text-white px-6 py-2 rounded-md hover:bg-yellow-600">Set User Password</button>
        </div>

        {/* Add Question */}
        <div className="bg-white p-6 rounded-lg shadow space-y-6">
          <h3 className="text-xl font-semibold mb-4">Add Quiz Question</h3>
          <form onSubmit={handleAddQuestion} className="grid grid-cols-2 gap-6">
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Type (e.g., MCQ)"
              value={formData.type}
              onChange={(e) => setFormData({ ...formData, type: e.target.value })}
              required
            />
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              type="number"
              placeholder="Timer (seconds)"
              value={formData.timer}
              onChange={(e) => setFormData({ ...formData, timer: e.target.value })}
              required
            />
            <textarea
              className="col-span-2 border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Question"
              value={formData.question}
              onChange={(e) => setFormData({ ...formData, question: e.target.value })}
              required
            />
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Option A"
              value={formData.option_a}
              onChange={(e) => setFormData({ ...formData, option_a: e.target.value })}
              required
            />
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Option B"
              value={formData.option_b}
              onChange={(e) => setFormData({ ...formData, option_b: e.target.value })}
              required
            />
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Option C"
              value={formData.option_c}
              onChange={(e) => setFormData({ ...formData, option_c: e.target.value })}
              required
            />
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Option D"
              value={formData.option_d}
              onChange={(e) => setFormData({ ...formData, option_d: e.target.value })}
              required
            />
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Correct Answer (A/B/C/D)"
              value={formData.correct_answer}
              onChange={(e) => setFormData({ ...formData, correct_answer: e.target.value })}
              required
            />
            <button type="submit" className="col-span-2 bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700">
              Add Question
            </button>
          </form>
        </div>

        {/* Delete Question */}
        <div className="bg-white p-6 rounded-lg shadow space-y-6">
          <h3 className="text-xl font-semibold mb-4">Delete Question</h3>
          <div className="flex items-center gap-6">
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-red-500"
              type="number"
              placeholder="Question ID to delete"
              value={questionIdToDelete}
              onChange={(e) => setQuestionIdToDelete(e.target.value)}
            />
            <button
              onClick={handleDeleteQuestion}
              className="bg-red-600 text-white px-6 py-3 rounded-md hover:bg-red-700"
            >
              Delete
            </button>
          </div>
        </div>

        {/* User Scores */}
        <div className="bg-white p-6 rounded-lg shadow space-y-6">
          <h3 className="text-xl font-semibold mb-4">User Scores</h3>
          <table className="w-full table-auto border">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-4 py-2">User</th>
                <th className="border px-4 py-2">Score</th>
                <th className="border px-4 py-2">Date</th>
              </tr>
            </thead>
            <tbody>
              {scores.map((score) => (
                <tr key={score.id}>
                  <td className="border px-4 py-2">{score.username}</td>
                  <td className="border px-4 py-2">{score.score}</td>
                  <td className="border px-4 py-2">{new Date(score.date_taken).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Set Common Password */}
        <div className="bg-white p-6 rounded-lg shadow space-y-6">
          <h3 className="text-xl font-semibold mb-4">Set Common User Password</h3>
          <div className="flex items-center gap-6">
            <input
              className="border p-3 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-green-500"
              type="password"
              placeholder="New user password"
              value={commonUserPassword}
              onChange={(e) => setCommonUserPassword(e.target.value)}
            />
            <button
              onClick={handleSetUserPassword}
              className="bg-green-600 text-white px-6 py-3 rounded-md hover:bg-green-700"
            >
