import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import AdminPage from './pages/AdminPage';
import UserPage from './pages/UserPage';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token')); // JWT Token from LocalStorage

  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear token on logout
    setToken(null); // Update state
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header" style={{ textAlign: 'center', padding: '20px' }}>
          <h1>Quiz Application</h1>
        </header>

        {/* Navigation Links */}
        <nav style={{ textAlign: 'center', marginBottom: '20px' }}>
          {token ? (
            <>
              <Link to="/admin" style={{ margin: '0 10px', fontSize: '18px' }}>Admin Panel</Link>
              <span>|</span>
              <Link to="/quiz" style={{ margin: '0 10px', fontSize: '18px' }}>Take Quiz</Link>
              <span>|</span>
              <button onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <Link to="/" style={{ margin: '0 10px', fontSize: '18px' }}>Login</Link>
          )}
        </nav>

        {/* Routes for different pages */}
        <Routes>
          <Route path="/" element={<Login setToken={setToken} />} />
          <Route path="/admin" element={<AdminPage token={token} />} />
          <Route path="/quiz" element={<UserPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
