import React, { useState, useEffect } from 'react';
import { Tabs, Tab, Box, Typography, CircularProgress, Button, Card, CardContent, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddQuestion from './AddQuestion';
import ViewScores from './ViewScores'; // Will create later

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function AdminDashboard() {
  const [tab, setTab] = useState(0);
  const [quizData, setQuizData] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
  };

  // Fetch quiz data
  useEffect(() => {
    fetch('http://localhost:3000/quiz')
      .then((res) => res.json())
      .then((data) => {
        setQuizData(data);
        setLoading(false);
      })
      .catch((error) => console.error('Error fetching quiz data:', error));
  }, []);

  // Handle delete question
  const handleDelete = (id) => {
    fetch(`http://localhost:3000/delete-quiz/${id}`, {
      method: 'DELETE',
    })
      .then(() => {
        setQuizData(quizData.filter((question) => question.id !== id)); // Remove deleted question from the state
        alert('Question deleted successfully');
      })
      .catch((error) => {
        console.error('Error deleting quiz question:', error);
      });
  };

  return (
    <Box sx={{ width: '100%' }}>
      <Typography variant="h4" sx={{ textAlign: 'center', mt: 2 }}>
        Admin Dashboard
      </Typography>

      <Tabs value={tab} onChange={handleTabChange} centered sx={{ mt: 2 }}>
        <Tab label="Add Question" />
        <Tab label="Delete Question" />
        <Tab label="User Scores" />
      </Tabs>

      <TabPanel value={tab} index={0}>
        <AddQuestion />
      </TabPanel>

      <TabPanel value={tab} index={1}>
        <h3>Delete Question</h3>
        {loading ? (
          <CircularProgress />
        ) : (
          <div>
            {quizData.length === 0 ? (
              <Typography>No questions available to delete</Typography>
            ) : (
              quizData.map((question) => (
                <Card key={question.id} sx={{ mb: 2 }}>
                  <CardContent>
                    <Typography variant="h6">Q: {question.question}</Typography>
                    <div>
                      <Button
                        variant="outlined"
                        color="error"
                        onClick={() => handleDelete(question.id)}
                        startIcon={<DeleteIcon />}
                      >
                        Delete
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        )}
      </TabPanel>

      <TabPanel value={tab} index={2}>
        <ViewScores />
      </TabPanel>
    </Box>
  );
}

export default AdminDashboard;
