import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AdminPage.css'; // Import the CSS file

function AdminPage() {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [formData, setFormData] = useState({
    type: '',
    question: '',
    option_a: '',
    option_b: '',
    option_c: '', 
    option_d: '', 
    correct_answer: '', 
    timer: ''
  });
  const [questionIdToDelete, setQuestionIdToDelete] = useState('');
  const [commonUserPassword, setCommonUserPassword] = useState('');
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('add');

  const backendUrl = 'http://localhost:3000';

  const fetchQuestions = async () => {
    try {
      const res = await axios.get(`${backendUrl}/quiz`);
      setQuestions(res.data);
    } catch (err) {
      console.error('Error fetching questions:', err);
    }
  };

  const fetchScores = async () => {
    try {
      const res = await axios.get(`${backendUrl}/scores`);
      setScores(res.data);
    } catch (err) {
      console.error('Error fetching scores:', err);
    }
  };

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  const handleAddQuestion = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${backendUrl}/add-question`, formData);
      setMessage(res.data);
      setFormData({
        type: '', question: '', option_a: '', option_b: '',
        option_c: '', option_d: '', correct_answer: '', timer: ''
      });
      fetchQuestions();
    } catch (err) {
      console.error('Error adding question:', err);
      setMessage('Failed to add question');
    }
  };

  const handleDeleteQuestion = async () => {
    if (!questionIdToDelete) return;
    try {
      const res = await axios.delete(`${backendUrl}/delete-quiz/${questionIdToDelete}`);
      setMessage(res.data);
      setQuestionIdToDelete('');
      fetchQuestions();
    } catch (err) {
      console.error('Error deleting question:', err);
      setMessage('Failed to delete question');
    }
  };

  const handleSetUserPassword = async () => {
    try {
      const res = await axios.post(`${backendUrl}/set-user-password`, { password: commonUserPassword });
      setMessage(res.data);
    } catch (err) {
      console.error('Error setting user password:', err);
      setMessage('Failed to set password');
    }
  };

  const handleLogout = () => {
    alert('Logged out');
  };

  const navItems = [
    { key: 'add', label: '➕ Add Question' },
    { key: 'delete', label: '🗑️ Delete Question' },
    { key: 'scores', label: '📊 User Scores' },
    { key: 'password', label: '🔐 Set Password' },
    { key: 'all', label: '📋 All Questions' },
  ];

  return (
    <div className="admin-container">
      <nav className="navbar">
        <div className="logo">
          <img src="/path-to-logo.png" alt="Logo" className="logo-img" />
        </div>
        <div className="nav-buttons">
          {navItems.map((item) => (
            <button
              key={item.key}
              onClick={() => setActiveTab(item.key)}
              className={`nav-btn ${activeTab === item.key ? 'active' : ''}`}
            >
              {item.label}
            </button>
          ))}
        </div>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </nav>

      <main className="main-content">
        <h2 className="header">📚 Admin Dashboard</h2>
        {message && <div className="message">{message}</div>}

        {/* Add Question */}
        {activeTab === 'add' && (
          <section className="form-section">
            <h3 className="form-title">Add New Question</h3>
            <form onSubmit={handleAddQuestion} className="form-grid">
              <div className="input-row">
                <input className="input-field" placeholder="Type (e.g., MCQ)" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })} required />
                <input className="input-field" type="number" placeholder="Timer (seconds)" value={formData.timer} onChange={(e) => setFormData({ ...formData, timer: e.target.value })} required />
              </div>
              <textarea className="input-field full-width" placeholder="Question" value={formData.question} onChange={(e) => setFormData({ ...formData, question: e.target.value })} required />
              <div className="input-row">
                <input className="input-field" placeholder="Option A" value={formData.option_a} onChange={(e) => setFormData({ ...formData, option_a: e.target.value })} required />
                <input className="input-field" placeholder="Option B" value={formData.option_b} onChange={(e) => setFormData({ ...formData, option_b: e.target.value })} required />
                <input className="input-field" placeholder="Option C" value={formData.option_c} onChange={(e) => setFormData({ ...formData, option_c: e.target.value })} required />
                <input className="input-field" placeholder="Option D" value={formData.option_d} onChange={(e) => setFormData({ ...formData, option_d: e.target.value })} required />
              </div>
              <div className="input-row">
                <input className="input-field" placeholder="Correct Answer (A/B/C/D)" value={formData.correct_answer} onChange={(e) => setFormData({ ...formData, correct_answer: e.target.value })} required />
              </div>
              <button type="submit" className="btn-submit">➕ Add Question</button>
            </form>
          </section>
        )}

        {/* Delete Question */}
        {activeTab === 'delete' && (
          <section className="form-section">
            <h3 className="form-title">Delete a Question</h3>
            <div className="delete-form">
              <input className="input-field" type="number" placeholder="Question ID" value={questionIdToDelete} onChange={(e) => setQuestionIdToDelete(e.target.value)} />
              <button onClick={handleDeleteQuestion} className="btn-delete">🗑️ Delete</button>
            </div>
          </section>
        )}

        {/* User Scores */}
        {activeTab === 'scores' && (
          <section className="table-section">
            <h3 className="form-title">User Scores</h3>
            <table className="score-table">
              <thead>
                <tr>
                  <th>User</th>
                  <th>Score</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {scores.map(score => (
                  <tr key={score.id}>
                    <td>{score.username}</td>
                    <td>{score.score}</td>
                    <td>{new Date(score.date_taken).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {/* Set Password */}
        {activeTab === 'password' && (
          <section className="form-section">
            <h3 className="form-title">Set Common User Password</h3>
            <div className="password-form">
              <input className="input-field" type="password" placeholder="New user password" value={commonUserPassword} onChange={(e) => setCommonUserPassword(e.target.value)} />
              <button onClick={handleSetUserPassword} className="btn-password">Set Password</button>
            </div>
          </section>
        )}

        {/* All Questions */}
        {activeTab === 'all' && (
          <section className="questions-list">
            <h3 className="form-title">All Questions</h3>
            <ul>
              {questions.map(q => (
                <li key={q.id} className="question-item">
                  <div className="question-header">Q{q.id}: {q.question} <span>(Timer: {q.timer}s)</span></div>
                  <div className="question-type">Type: {q.type}</div>
                  <ul className="options-list">
                    <li>A. {q.option_a}</li>
                    <li>B. {q.option_b}</li>
                    <li>C. {q.option_c}</li>
                    <li>D. {q.option_d}</li>
                  </ul>
                  <div className="correct-answer">Correct: {q.correct_answer}</div>
                </li>
              ))}
            </ul>
          </section>
        )}
      </main>
    </div>
  );
}

export default AdminPage;
