import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import AdminPage from './pages/AdminPage'; // Admin Panel
import UserPage from './pages/UserPage';   // User Quiz Page
import Login from './components/Login';    // Optional: Login Component

function App() {
  return (
    <Router>
      <div className="App">
        {/* Header */}
        <header className="App-header" style={{ textAlign: 'center', padding: '20px' }}>
          <h1>Quiz Application</h1>
        </header>

        /*{/* Navigation Links */}
        <nav style={{ textAlign: 'center', marginBottom: '20px' }}>
          <Link to="/admin" style={{ margin: '0 10px', fontSize: '18px' }}>
            Admin Panel
          </Link>
          <span>|</span>
          <Link to="/quiz" style={{ margin: '0 10px', fontSize: '18px' }}>
            Take Quiz
          </Link>
        </nav>

        {/* Routes for different pages */}
        <Routes>
          <Route path="/" element={<Login />} /> {/* Optional Login */}
          <Route path="/admin" element={<AdminPage />} />  {/* Admin Panel Route */}
          <Route path="/quiz" element={<UserPage />} />   {/* Take Quiz Route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
