import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, Typography, Box } from '@mui/material';

function AdminLogin() {
  const [adminID, setAdminID] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    // Dummy logic, replace with real API call
    if (adminID === 'admin' && password === 'admin123') {
      localStorage.setItem('adminToken', 'validToken');
      navigate('/admin');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <Box sx={{ textAlign: 'center', mt: 10 }}>
      <Typography variant="h4">Admin Login</Typography>
      <TextField label="Admin ID" value={adminID} onChange={(e) => setAdminID(e.target.value)} sx={{ m: 1 }} />
      <TextField label="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} sx={{ m: 1 }} />
      <br />
      <Button variant="contained" onClick={handleLogin}>Login</Button>
    </Box>
  );
}

export default AdminLogin;
