import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // üëà added

function UserLogin() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // üëà added

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3000/user-login', {
        userId,
        password,
      });

      setMessage(response.data.message);
      setError('');
      localStorage.setItem('token', response.data.token);

      // üëá navigate to quiz after successful login
      navigate('/quiz');
    } catch (err) {
      if (err.response && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError('‚ùå Server error or wrong credentials');
      }
    }
  };

  return (
    <div>
      <h2>User Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {message && <p style={{ color: 'green' }}>{message}</p>}
      <input
        type="text"
        placeholder="User ID"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login & Start Quiz</button>
    </div>
  );
}

export default UserLogin;
