import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPage() {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [formData, setFormData] = useState({
    type: '',
     question: '',
    option_a: '',
    option_b: '',
    option_c: '', 
    option_d: '', 
    correct_answer: '', 
    timer: ''
  });
  const [questionIdToDelete, setQuestionIdToDelete] = useState('');
  const [commonUserPassword, setCommonUserPassword] = useState('');
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('add');

  const backendUrl = 'http://localhost:3000';

  const fetchQuestions = async () => {
    try {
      const res = await axios.get(`${backendUrl}/quiz`);
      setQuestions(res.data);
    } catch (err) {
      console.error('Error fetching questions:', err);
    }
  };

  const fetchScores = async () => {
    try {
      const res = await axios.get(`${backendUrl}/scores`);
      setScores(res.data);
    } catch (err) {
      console.error('Error fetching scores:', err);
    }
  };

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  const handleAddQuestion = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${backendUrl}/add-question`, formData);
      setMessage(res.data);
      setFormData({
        type: '', question: '', option_a: '', option_b: '',
        option_c: '', option_d: '', correct_answer: '', timer: ''
      });
      fetchQuestions();
    } catch (err) {
      console.error('Error adding question:', err);
      setMessage('Failed to add question');
    }
  };

  const handleDeleteQuestion = async () => {
    if (!questionIdToDelete) return;
    try {
      const res = await axios.delete(`${backendUrl}/delete-quiz/${questionIdToDelete}`);
      setMessage(res.data);
      setQuestionIdToDelete('');
      fetchQuestions();
    } catch (err) {
      console.error('Error deleting question:', err);
      setMessage('Failed to delete question');
    }
  };

  const handleSetUserPassword = async () => {
    try {
      const res = await axios.post(`${backendUrl}/set-user-password`, { password: commonUserPassword });
      setMessage(res.data);
    } catch (err) {
      console.error('Error setting user password:', err);
      setMessage('Failed to set password');
    }
  };

  const navItems = [
    { key: 'add', label: '‚ûï Add Question' },
    { key: 'delete', label: 'üóëÔ∏è Delete Question' },
    { key: 'scores', label: 'üìä User Scores' },
    { key: 'password', label: 'üîê Set Password' },
    { key: 'all', label: 'üìã All Questions' },
  ];

  return (
    <div className="min-h-screen bg-gray-50 font-inter">
      <nav className="bg-gradient-to-r from-blue-800 to-blue-600 text-white px-8 py-5 shadow-md flex flex-wrap justify-center gap-4">
        {navItems.map((item) => (
          <button
            key={item.key}
            onClick={() => setActiveTab(item.key)}
            className={`px-4 py-2 rounded-full text-sm font-semibold transition duration-300 shadow-sm 
              ${activeTab === item.key ? 'bg-white text-blue-800' : 'hover:bg-blue-700 bg-blue-900'}`}
          >
            {item.label}
          </button>
        ))}
      </nav>

      <main className="max-w-6xl mx-auto px-6 py-10">
        <h2 className="text-4xl font-bold text-blue-900 mb-6 text-center">üìö Admin Dashboard</h2>
        {message && <div className="mb-4 p-3 rounded bg-green-100 text-green-700 font-medium shadow-md">{message}</div>}

        {/* Add Question */}
        {activeTab === 'add' && (
          <section className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold text-blue-700 mb-4">Add New Question</h3>
            <form onSubmit={handleAddQuestion} className="grid md:grid-cols-2 gap-6">
              <input className="input" placeholder="Type (e.g., MCQ)" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })} required />
              <input className="input" type="number" placeholder="Timer (seconds)" value={formData.timer} onChange={(e) => setFormData({ ...formData, timer: e.target.value })} required />
              <textarea className="input md:col-span-2" placeholder="Question" value={formData.question} onChange={(e) => setFormData({ ...formData, question: e.target.value })} required />
              <input className="input" placeholder="Option A" value={formData.option_a} onChange={(e) => setFormData({ ...formData, option_a: e.target.value })} required />
              <input className="input" placeholder="Option B" value={formData.option_b} onChange={(e) => setFormData({ ...formData, option_b: e.target.value })} required />
              <input className="input" placeholder="Option C" value={formData.option_c} onChange={(e) => setFormData({ ...formData, option_c: e.target.value })} required />
              <input className="input" placeholder="Option D" value={formData.option_d} onChange={(e) => setFormData({ ...formData, option_d: e.target.value })} required />
              <input className="input" placeholder="Correct Answer (A/B/C/D)" value={formData.correct_answer} onChange={(e) => setFormData({ ...formData, correct_answer: e.target.value })} required />
              <button type="submit" className="md:col-span-2 bg-blue-700 text-white py-2 rounded shadow hover:bg-blue-800 transition">‚ûï Add Question</button>
            </form>
          </section>
        )}

        {/* Delete Question */}
        {activeTab === 'delete' && (
          <section className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold text-red-600 mb-4">Delete a Question</h3>
            <div className="flex flex-wrap gap-4 items-center">
              <input className="input" type="number" placeholder="Question ID" value={questionIdToDelete} onChange={(e) => setQuestionIdToDelete(e.target.value)} />
              <button onClick={handleDeleteQuestion} className="bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700">üóëÔ∏è Delete</button>
            </div>
          </section>
        )}


        {/* User Scores */}
        {activeTab === 'scores' && (
          <section className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold text-gray-700 mb-4">User Scores</h3>
            <table className="w-full table-auto text-sm border">
              <thead>
                <tr className="bg-gray-200 text-gray-600">
                  <th className="p-3 text-left">User</th>
                  <th className="p-3 text-left">Score</th>
                  <th className="p-3 text-left">Date</th>
                </tr>
              </thead>
              <tbody>
                {scores.map(score => (
                  <tr key={score.id} className="border-b hover:bg-gray-50">
                    <td className="p-3">{score.username}</td>
                    <td className="p-3">{score.score}</td>
                    <td className="p-3">{new Date(score.date_taken).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {/* Set Password */}
        {activeTab === 'password' && (
          <section className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold text-purple-700 mb-4">Set Common User Password</h3>
            <div className="flex flex-wrap gap-4 items-center">
              <input className="input" type="password" placeholder="New user password" value={commonUserPassword} onChange={(e) => setCommonUserPassword(e.target.value)} />
              <button onClick={handleSetUserPassword} className="bg-purple-600 text-white px-6 py-2 rounded hover:bg-purple-700">Set Password</button>
            </div>
          </section>
        )}

        {/* All Questions */}
        {activeTab === 'all' && (
          <section className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold text-gray-800 mb-4">All Questions</h3>
            <ul className="space-y-6">
              {questions.map(q => (
                <li key={q.id} className="bg-gray-50 p-5 rounded shadow-sm">
                  <div className="font-semibold text-lg">Q{q.id}: {q.question} <span className="text-sm text-gray-500">(Timer: {q.timer}s)</span></div>
                  <div className="text-sm text-gray-600 mt-2">Type: {q.type}</div>
                  <ul className="mt-2 ml-4 list-disc text-gray-700 text-sm">
                    <li>A. {q.option_a}</li>
                    <li>B. {q.option_b}</li>
                    <li>C. {q.option_c}</li>
                    <li>D. {q.option_d}</li>
                  </ul>
                  <div className="mt-2 text-green-700 font-semibold">Correct: {q.correct_answer}</div>
                </li>
              ))}
            </ul>
          </section>
        )}
      </main>
    </div>
  );
}

export default AdminPage;
