import React, { useState } from 'react';

const AddQuestionForm = ({ onAddQuestion }) => {
  const [questionData, setQuestionData] = useState({
    type: '',
    question: '',
    option_a: '',
    option_b: '',
    option_c: '',
    option_d: '',
    correct_answer: '',
    timer: 0,
  });

  const handleChange = (e) => {
    setQuestionData({
      ...questionData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddQuestion(questionData);  // Call parent function to handle adding the question
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="question"
        value={questionData.question}
        onChange={handleChange}
        placeholder="Question"
      />
      <input
        type="text"
        name="option_a"
        value={questionData.option_a}
        onChange={handleChange}
        placeholder="Option A"
      />
      <input
        type="text"
        name="option_b"
        value={questionData.option_b}
        onChange={handleChange}
        placeholder="Option B"
      />
      <input
        type="text"
        name="option_c"
        value={questionData.option_c}
        onChange={handleChange}
        placeholder="Option C"
      />
      <input
        type="text"
        name="option_d"
        value={questionData.option_d}
        onChange={handleChange}
        placeholder="Option D"
      />
      <input
        type="text"
        name="correct_answer"
        value={questionData.correct_answer}
        onChange={handleChange}
        placeholder="Correct Answer"
      />
      <input
        type="number"
        name="timer"
        value={questionData.timer}
        onChange={handleChange}
        placeholder="Timer"
      />
      <button type="submit">Add Question</button>
    </form>
  );
};

export default AddQuestionForm;
