import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import AdminPage from './pages/AdminPage';
import UserPage from './pages/UserPage';
import LandingPage from './pages/LandingPage';
import AdminLogin from './components/AdminLogin';
import UserLogin from './pages/UserLogin';

function App() {
  const [adminToken, setAdminToken] = useState(localStorage.getItem('adminToken') || '');

  const handleLoginSuccess = (token) => {
    localStorage.setItem('adminToken', token);
    setAdminToken(token);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('userID');
    setAdminToken('');
  };

  useEffect(() => {
    if (!adminToken) return;
    const timer = setTimeout(() => handleLogout(), 60 * 60 * 1000); // 1 hour
    return () => clearTimeout(timer);
  }, [adminToken]);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/admin-login" element={<AdminLogin onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/admin" element={adminToken ? <AdminPage token={adminToken} /> : <Navigate to="/admin-login" />} />
        <Route path="/user-login" element={<UserLogin />} />
        <Route path="/quiz" element={localStorage.getItem('userID') ? <UserPage /> : <Navigate to="/user-login" />} />
        <Route path="*" element={<div style={{ textAlign: 'center' }}>404 Page Not Found</div>} />
      </Routes>
    </Router>
  );
}

export default App;
