import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserDashboard() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchQuizQuestions = async () => {
      try {
        const response = await axios.get('http://localhost:3000/quiz');
        setQuestions(response.data);  // Set the questions from backend
        setLoading(false);
      } catch (err) {
        setError('Failed to load quiz questions');
        setLoading(false);
      }
    };

    fetchQuizQuestions();
  }, []);

  const handleSubmit = () => {
    // Handle quiz submission
  };

  if (loading) {
    return <p>Loading quiz questions...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h2>Quiz Dashboard</h2>
      <form onSubmit={handleSubmit}>
        {questions.map((question, index) => (
          <div key={index}>
            <p>{question.question}</p> {/* Assuming your DB has a column named 'question' */}
            {question.option_a && (
              <label>
                <input type="radio" name={`question${index}`} value={question.option_a} />
                {question.option_a}
              </label>
            )}
            {question.option_b && (
              <label>
                <input type="radio" name={`question${index}`} value={question.option_b} />
                {question.option_b}
              </label>
            )}
            {question.option_c && (
              <label>
                <input type="radio" name={`question${index}`} value={question.option_c} />
                {question.option_c}
              </label>
            )}
            {question.option_d && (
              <label>
                <input type="radio" name={`question${index}`} value={question.option_d} />
                {question.option_d}
              </label>
            )}
          </div>
        ))}
        <button type="submit">Submit Quiz</button>
      </form>
    </div>
  );
}

export default UserDashboard;
