import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

function App() {
  // State to store the quiz questions
  const [quizData, setQuizData] = useState([]);

  // Fetch quiz data from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:3000/quiz') // Make sure the backend is running at this URL
      .then(response => response.json())
      .then(data => {
        console.log(data); // Log the fetched quiz data
        setQuizData(data); // Store the quiz data in state
      })
      .catch(error => {
        console.error('Error fetching quiz data:', error);
      });
  }, []);

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Quiz Application</h1>
        </header>

        {/* Navigation Links */}
        <nav>
          <Link to="/admin">Admin Panel</Link> | 
          <Link to="/quiz">Take Quiz</Link>
        </nav>

        {/* Routes */}
        <Routes>
          {/* Admin Panel Route */}
          <Route path="/admin" element={<AdminPanel />} />

          {/* Quiz Route */}
          <Route path="/quiz" element={<Quiz quizData={quizData} />} />
        </Routes>
      </div>
    </Router>
  );
}

// Admin Panel component (for example purposes, you can add more logic here)
function AdminPanel() {
  return (
    <div>
      <h2>Admin Panel</h2>
      <p>Admin can manage the quiz questions here.</p>
    </div>
  );
}

// Quiz Component to display the quiz questions
function Quiz({ quizData }) {
  return (
    <div>
      <h2>Take the Quiz</h2>
      {quizData.length === 0 ? (
        <p>Loading quiz questions...</p>
      ) : (
        <ul>
          {quizData.map((question) => (
            <li key={question.id}>
              <h3>{question.question}</h3>
              <ul>
                <li>{question.option_a}</li>
                <li>{question.option_b}</li>
                <li>{question.option_c}</li>
                <li>{question.option_d}</li>
              </ul>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
