import React, { useState } from 'react';
import axios from 'axios';

const UserLogin = ({ onLogin }) => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const res = await axios.post('http://localhost:3000/user-login', { userId, password });
      localStorage.setItem('userToken', res.data.token);
      onLogin(userId);
    } catch (err) {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-2">User Login</h2>
      <input type="text" placeholder="User ID" className="w-full p-2 mb-2 border" value={userId} onChange={(e) => setUserId(e.target.value)} />
      <input type="password" placeholder="Password" className="w-full p-2 mb-2 border" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin} className="bg-blue-500 text-white px-4 py-2 w-full">Login</button>
    </div>
  );
};

export default UserLogin;
