import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPage({ token }) {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [formData, setFormData] = useState({
    type: '',
    question: '',
    option_a: '',
    option_b: '',
    option_c: '',
    option_d: '',
    correct_answer: '',
    timer: ''
  });
  const [questionIdToDelete, setQuestionIdToDelete] = useState('');
  const [commonUserPassword, setCommonUserPassword] = useState('');
  const [message, setMessage] = useState('');

  const backendUrl = 'http://localhost:3000'; // Update if your backend runs elsewhere

  // Load existing quiz questions
  const fetchQuestions = async () => {
    try {
      const res = await axios.get(`${backendUrl}/quiz`);
      setQuestions(res.data);
    } catch (err) {
      console.error('Error fetching questions:', err);
    }
  };

  // Load user scores
  const fetchScores = async () => {
    try {
      const res = await axios.get(`${backendUrl}/scores`);
      setScores(res.data);
    } catch (err) {
      console.error('Error fetching scores:', err);
    }
  };

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  // Add Question Handler
  const handleAddQuestion = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${backendUrl}/add-question`, formData);
      setMessage(res.data);
      setFormData({
        type: '',
        question: '',
        option_a: '',
        option_b: '',
        option_c: '',
        option_d: '',
        correct_answer: '',
        timer: ''
      });
      fetchQuestions(); // Refresh questions
    } catch (err) {
      console.error('Error adding question:', err);
      setMessage('Failed to add question');
    }
  };

  // Delete Question Handler
  const handleDeleteQuestion = async () => {
    if (!questionIdToDelete) return;
    try {
      const res = await axios.delete(`${backendUrl}/delete-quiz/${questionIdToDelete}`);
      setMessage(res.data);
      setQuestionIdToDelete('');
      fetchQuestions();
    } catch (err) {
      console.error('Error deleting question:', err);
      setMessage('Failed to delete question');
    }
  };

  // Set Common User Password
  const handleSetUserPassword = async () => {
    try {
      const res = await axios.post(`${backendUrl}/set-user-password`, { password: commonUserPassword });
      setMessage(res.data);
    } catch (err) {
      console.error('Error setting user password:', err);
      setMessage('Failed to set password');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Panel</h2>

      {/* Message */}
      {message && <p style={{ color: 'green' }}>{message}</p>}

      {/* Add Question Form */}
      <h3>Add Quiz Question</h3>
      <form onSubmit={handleAddQuestion}>
        <input type="text" placeholder="Type (e.g., MCQ)" value={formData.type}
          onChange={(e) => setFormData({ ...formData, type: e.target.value })} required /><br />
        <textarea placeholder="Question" value={formData.question}
          onChange={(e) => setFormData({ ...formData, question: e.target.value })} required /><br />
        <input type="text" placeholder="Option A" value={formData.option_a}
          onChange={(e) => setFormData({ ...formData, option_a: e.target.value })} required /><br />
        <input type="text" placeholder="Option B" value={formData.option_b}
          onChange={(e) => setFormData({ ...formData, option_b: e.target.value })} required /><br />
        <input type="text" placeholder="Option C" value={formData.option_c}
          onChange={(e) => setFormData({ ...formData, option_c: e.target.value })} required /><br />
        <input type="text" placeholder="Option D" value={formData.option_d}
          onChange={(e) => setFormData({ ...formData, option_d: e.target.value })} required /><br />
        <input type="text" placeholder="Correct Answer (A/B/C/D)" value={formData.correct_answer}
          onChange={(e) => setFormData({ ...formData, correct_answer: e.target.value })} required /><br />
        <input type="number" placeholder="Timer (in seconds)" value={formData.timer}
          onChange={(e) => setFormData({ ...formData, timer: e.target.value })} required /><br />
        <button type="submit">Add Question</button>
      </form>

      <hr />

      {/* Delete Question */}
      <h3>Delete Question</h3>
      <input type="number" placeholder="Question ID to delete" value={questionIdToDelete}
        onChange={(e) => setQuestionIdToDelete(e.target.value)} />
      <button onClick={handleDeleteQuestion}>Delete</button>

      <hr />

      {/* View Scores */}
      <h3>User Scores</h3>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>User</th>
            <th>Score</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {scores.map(score => (
            <tr key={score.id}>
              <td>{score.username}</td>
              <td>{score.score}</td>
              <td>{new Date(score.date_taken).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <hr />

      {/* Set User Password */}
      <h3>Set Common User Password</h3>
      <input type="password" placeholder="New user password"
        value={commonUserPassword}
        onChange={(e) => setCommonUserPassword(e.target.value)} />
      <button onClick={handleSetUserPassword}>Set Password</button>

      <hr />

      {/* View All Questions */}
      <h3>All Quiz Questions</h3>
      <ul>
        {questions.map(q => (
          <li key={q.id}>
            <strong>Q{q.id}</strong>: {q.question} (Timer: {q.timer}s)
            <ul>
              <li>A: {q.option_a}</li>
              <li>B: {q.option_b}</li>
              <li>C: {q.option_c}</li>
              <li>D: {q.option_d}</li>
              <li><strong>Answer:</strong> {q.correct_answer}</li>
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminPage;
