import React, { useState } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  Paper,
} from '@mui/material';

function AddQuestion() {
  const [form, setForm] = useState({
    type: 'multiple_choice',
    question: '',
    option_a: '',
    option_b: '',
    option_c: '',
    option_d: '',
    correct_answer: '',
    timer: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    // Prepare payload without ID (PostgreSQL will auto-generate it)
    const { type, question, option_a, option_b, option_c, option_d, correct_answer, timer } = form;
    const payload = {
      type,
      question,
      option_a,
      option_b,
      option_c,
      option_d,
      correct_answer,
      timer: parseInt(timer), // Ensure timer is a number
    };

    try {
      const response = await fetch('http://localhost:3000/add-question', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        setMessage('✅ Question added successfully!');
        setForm({
          type: 'multiple_choice',
          question: '',
          option_a: '',
          option_b: '',
          option_c: '',
          option_d: '',
          correct_answer: '',
          timer: '',
        });
      } else {
        const errorText = await response.text();
        setMessage(`❌ Failed: ${errorText}`);
      }
    } catch (err) {
      console.error('Error:', err);
      setMessage('❌ Error connecting to backend');
    }
  };

  return (
    <Paper elevation={3} sx={{ padding: 3, maxWidth: 600, margin: 'auto' }}>
      <Typography variant="h5" gutterBottom>
        Add New Question
      </Typography>

      <TextField
        fullWidth
        label="Question"
        name="question"
        value={form.question}
        onChange={handleChange}
        margin="normal"
      />
      <TextField fullWidth label="Option A" name="option_a" value={form.option_a} onChange={handleChange} margin="normal" />
      <TextField fullWidth label="Option B" name="option_b" value={form.option_b} onChange={handleChange} margin="normal" />
      <TextField fullWidth label="Option C" name="option_c" value={form.option_c} onChange={handleChange} margin="normal" />
      <TextField fullWidth label="Option D" name="option_d" value={form.option_d} onChange={handleChange} margin="normal" />
      <TextField
        fullWidth
        label="Correct Answer"
        name="correct_answer"
        value={form.correct_answer}
        onChange={handleChange}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Timer (in seconds)"
        name="timer"
        value={form.timer}
        onChange={handleChange}
        type="number"
        margin="normal"
      />

      <Box sx={{ mt: 2 }}>
        <Button variant="contained" onClick={handleSubmit}>
          Submit Question
        </Button>
      </Box>

      {message && (
        <Typography sx={{ mt: 2 }} color="primary">
          {message}
        </Typography>
      )}
    </Paper>
  );
}

export default AddQuestion;
