import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPage() {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [formData, setFormData] = useState({
    type: '', question: '', option_a: '', option_b: '',
    option_c: '', option_d: '', correct_answer: '', timer: ''
  });
  const [questionIdToDelete, setQuestionIdToDelete] = useState('');
  const [commonUserPassword, setCommonUserPassword] = useState('');
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('add');

  const backendUrl = 'http://localhost:3000';

  const fetchQuestions = async () => {
    try {
      const res = await axios.get(`${backendUrl}/quiz`);
      setQuestions(res.data);
    } catch (err) {
      console.error('Error fetching questions:', err);
    }
  };

  const fetchScores = async () => {
    try {
      const res = await axios.get(`${backendUrl}/scores`);
      setScores(res.data);
    } catch (err) {
      console.error('Error fetching scores:', err);
    }
  };

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  const handleAddQuestion = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${backendUrl}/add-question`, formData);
      setMessage(res.data);
      setFormData({
        type: '', question: '', option_a: '', option_b: '',
        option_c: '', option_d: '', correct_answer: '', timer: ''
      });
      fetchQuestions();
    } catch (err) {
      console.error('Error adding question:', err);
      setMessage('Failed to add question');
    }
  };

  const handleDeleteQuestion = async () => {
    if (!questionIdToDelete) return;
    try {
      const res = await axios.delete(`${backendUrl}/delete-quiz/${questionIdToDelete}`);
      setMessage(res.data);
      setQuestionIdToDelete('');
      fetchQuestions();
    } catch (err) {
      console.error('Error deleting question:', err);
      setMessage('Failed to delete question');
    }
  };

  const handleSetUserPassword = async () => {
    try {
      const res = await axios.post(`${backendUrl}/set-user-password`, { password: commonUserPassword });
      setMessage(res.data);
    } catch (err) {
      console.error('Error setting user password:', err);
      setMessage('Failed to set password');
    }
  };

  const navItems = [
    { key: 'add', label: '➕ Add Question' },
    { key: 'delete', label: '🗑️ Delete Question' },
    { key: 'scores', label: '📊 User Scores' },
    { key: 'password', label: '🔐 Set Password' },
    { key: 'all', label: '📋 All Questions' },
  ];

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      {/* Top Navigation Bar */}
      <nav className="bg-blue-800 text-white shadow-md px-6 py-4 flex flex-wrap justify-center gap-4">
        {navItems.map((item) => (
          <button
            key={item.key}
            onClick={() => setActiveTab(item.key)}
            className={`px-4 py-2 rounded-full text-sm font-medium transition 
              ${activeTab === item.key ? 'bg-blue-950' : 'hover:bg-blue-700'}`}
          >
            {item.label}
          </button>
        ))}
      </nav>

      <main className="max-w-5xl mx-auto p-6">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">📚 Admin Panel</h2>
        {message && <div className="mb-4 p-3 rounded bg-green-100 text-green-700 font-medium">{message}</div>}

        {/* Section: Add Question */}
        {activeTab === 'add' && (
          <div className="bg-white p-6 rounded-xl shadow-md space-y-4">
            <h3 className="text-xl font-semibold text-blue-700">Add a New Question</h3>
            <form onSubmit={handleAddQuestion} className="grid md:grid-cols-2 gap-4">
              <input className="input-style" placeholder="Type (e.g., MCQ)" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })} required />
              <input className="input-style" type="number" placeholder="Timer (seconds)" value={formData.timer} onChange={(e) => setFormData({ ...formData, timer: e.target.value })} required />
              <textarea className="input-style md:col-span-2" placeholder="Question" value={formData.question} onChange={(e) => setFormData({ ...formData, question: e.target.value })} required />
              <input className="input-style" placeholder="Option A" value={formData.option_a} onChange={(e) => setFormData({ ...formData, option_a: e.target.value })} required />
              <input className="input-style" placeholder="Option B" value={formData.option_b} onChange={(e) => setFormData({ ...formData, option_b: e.target.value })} required />
              <input className="input-style" placeholder="Option C" value={formData.option_c} onChange={(e) => setFormData({ ...formData, option_c: e.target.value })} required />
              <input className="input-style" placeholder="Option D" value={formData.option_d} onChange={(e) => setFormData({ ...formData, option_d: e.target.value })} required />
              <input className="input-style" placeholder="Correct Answer (A/B/C/D)" value={formData.correct_answer} onChange={(e) => setFormData({ ...formData, correct_answer: e.target.value })} required />
              <button type="submit" className="md:col-span-2 bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition">Add Question</button>
            </form>
          </div>
        )}

        {/* Section: Delete Question */}
        {activeTab === 'delete' && (
          <div className="bg-white p-6 rounded-xl shadow-md space-y-4">
            <h3 className="text-xl font-semibold text-red-600">Delete a Question</h3>
            <div className="flex gap-4 items-center">
              <input className="input-style" type="number" placeholder="Question ID" value={questionIdToDelete} onChange={(e) => setQuestionIdToDelete(e.target.value)} />
              <button onClick={handleDeleteQuestion} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">Delete</button>
            </div>
          </div>
        )}

        {/* Section: User Scores */}
        {activeTab === 'scores' && (
          <div className="bg-white p-6 rounded-xl shadow-md">
            <h3 className="text-xl font-semibold text-gray-700 mb-4">User Scores</h3>
            <table className="w-full table-auto text-sm">
              <thead>
                <tr className="bg-gray-200 text-gray-600">
                  <th className="p-2 text-left">User</th>
                  <th className="p-2 text-left">Score</th>
                  <th className="p-2 text-left">Date</th>
                </tr>
              </thead>
              <tbody>
                {scores.map(score => (
                  <tr key={score.id} className="border-b hover:bg-gray-50">
                    <td className="p-2">{score.username}</td>
                    <td className="p-2">{score.score}</td>
                    <td className="p-2">{new Date(score.date_taken).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Section: Set Password */}
        {activeTab === 'password' && (
          <div className="bg-white p-6 rounded-xl shadow-md space-y-4">
            <h3 className="text-xl font-semibold text-purple-700">Set Common User Password</h3>
            <div className="flex gap-4 items-center">
              <input className="input-style" type="password" placeholder="New user password" value={commonUserPassword} onChange={(e) => setCommonUserPassword(e.target.value)} />
              <button onClick={handleSetUserPassword} className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700">Set Password</button>
            </div>
          </div>
        )}

        {/* Section: All Questions */}
        {activeTab === 'all' && (
          <div className="bg-white p-6 rounded-xl shadow-md space-y-4">
            <h3 className="text-xl font-semibold text-gray-800">All Questions</h3>
            <ul className="space-y-4">
              {questions.map(q => (
                <li key={q.id} className="bg-gray-50 p-4 rounded shadow-sm">
                  <div className="font-semibold">Q{q.id}: {q.question} <span className="text-sm text-gray-500">(Timer: {q.timer}s)</span></div>
                  <ul className="ml-4 list-disc text-gray-700">
                    <li>A: {q.option_a}</li>
                    <li>B: {q.option_b}</li>
                    <li>C: {q.option_c}</li>
                    <li>D: {q.option_d}</li>
                    <li className="font-medium text-green-700">Correct Answer: {q.correct_answer}</li>
                  </ul>
                </li>
              ))}
            </ul>
          </div>
        )}
      </main>

      {/* Custom Tailwind Classes */}
      <style jsx>{`
        .input-style {
          @apply border border-gray-300 p-2 rounded w-full focus:ring-2 focus:ring-blue-500 outline-none transition;
        }
      `}</style>
    </div>
  );
}

export default AdminPage;
