import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPage({ token }) {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [formData, setFormData] = useState({
    type: '', question: '', option_a: '', option_b: '',
    option_c: '', option_d: '', correct_answer: '', timer: ''
  });
  const [questionIdToDelete, setQuestionIdToDelete] = useState('');
  const [commonUserPassword, setCommonUserPassword] = useState('');
  const [message, setMessage] = useState('');

  const backendUrl = 'http://localhost:3000';

  const fetchQuestions = async () => {
    try {
      const res = await axios.get(`${backendUrl}/quiz`);
      setQuestions(res.data);
    } catch (err) {
      console.error('Error fetching questions:', err);
    }
  };

  const fetchScores = async () => {
    try {
      const res = await axios.get(`${backendUrl}/scores`);
      setScores(res.data);
    } catch (err) {
      console.error('Error fetching scores:', err);
    }
  };

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  const handleAddQuestion = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${backendUrl}/add-question`, formData);
      setMessage(res.data);
      setFormData({
        type: '', question: '', option_a: '', option_b: '',
        option_c: '', option_d: '', correct_answer: '', timer: ''
      });
      fetchQuestions();
    } catch (err) {
      console.error('Error adding question:', err);
      setMessage('Failed to add question');
    }
  };

  const handleDeleteQuestion = async () => {
    if (!questionIdToDelete) return;
    try {
      const res = await axios.delete(`${backendUrl}/delete-quiz/${questionIdToDelete}`);
      setMessage(res.data);
      setQuestionIdToDelete('');
      fetchQuestions();
    } catch (err) {
      console.error('Error deleting question:', err);
      setMessage('Failed to delete question');
    }
  };

  const handleSetUserPassword = async () => {
    try {
      const res = await axios.post(`${backendUrl}/set-user-password`, { password: commonUserPassword });
      setMessage(res.data);
    } catch (err) {
      console.error('Error setting user password:', err);
      setMessage('Failed to set password');
    }
  };

  return (
    <div className="scroll-smooth">
      {/* Top Navbar */}
      <nav className="fixed top-0 w-full bg-blue-800 text-white px-4 py-3 shadow z-10 flex gap-6">
        <a href="#add-question" className="hover:underline">Add Question</a>
        <a href="#delete-question" className="hover:underline">Delete Question</a>
        <a href="#view-scores" className="hover:underline">View Scores</a>
        <a href="#set-password" className="hover:underline">Set Password</a>
        <a href="#all-questions" className="hover:underline">All Questions</a>
      </nav>

      <div className="pt-20 px-6 space-y-8">
        <h2 className="text-3xl font-semibold">Admin Panel</h2>
        {message && <p className="text-green-600 font-medium">{message}</p>}

        {/* Add Question */}
        <div id="add-question" className="border rounded-lg p-4 shadow space-y-4">
          <h3 className="text-xl font-semibold">Add Quiz Question</h3>
          <form onSubmit={handleAddQuestion} className="grid grid-cols-2 gap-4">
            <input className="border p-2 rounded" placeholder="Type (e.g., MCQ)" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })} required />
            <input className="border p-2 rounded" type="number" placeholder="Timer (seconds)" value={formData.timer} onChange={(e) => setFormData({ ...formData, timer: e.target.value })} required />
            <textarea className="col-span-2 border p-2 rounded" placeholder="Question" value={formData.question} onChange={(e) => setFormData({ ...formData, question: e.target.value })} required />
            <input className="border p-2 rounded" placeholder="Option A" value={formData.option_a} onChange={(e) => setFormData({ ...formData, option_a: e.target.value })} required />
            <input className="border p-2 rounded" placeholder="Option B" value={formData.option_b} onChange={(e) => setFormData({ ...formData, option_b: e.target.value })} required />
            <input className="border p-2 rounded" placeholder="Option C" value={formData.option_c} onChange={(e) => setFormData({ ...formData, option_c: e.target.value })} required />
            <input className="border p-2 rounded" placeholder="Option D" value={formData.option_d} onChange={(e) => setFormData({ ...formData, option_d: e.target.value })} required />
            <input className="border p-2 rounded" placeholder="Correct Answer (A/B/C/D)" value={formData.correct_answer} onChange={(e) => setFormData({ ...formData, correct_answer: e.target.value })} required />
            <button type="submit" className="col-span-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Add Question</button>
          </form>
        </div>

        {/* Delete Question */}
        <div id="delete-question" className="border rounded-lg p-4 shadow space-y-4">
          <h3 className="text-xl font-semibold">Delete Question</h3>
          <div className="flex items-center gap-4">
            <input className="border p-2 rounded" type="number" placeholder="Question ID to delete" value={questionIdToDelete} onChange={(e) => setQuestionIdToDelete(e.target.value)} />
            <button onClick={handleDeleteQuestion} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">Delete</button>
          </div>
        </div>

        {/* View Scores */}
        <div id="view-scores" className="border rounded-lg p-4 shadow space-y-4">
          <h3 className="text-xl font-semibold">User Scores</h3>
          <table className="w-full table-auto border">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-4 py-2">User</th>
                <th className="border px-4 py-2">Score</th>
                <th className="border px-4 py-2">Date</th>
              </tr>
            </thead>
            <tbody>
              {scores.map(score => (
                <tr key={score.id}>
                  <td className="border px-4 py-2">{score.username}</td>
                  <td className="border px-4 py-2">{score.score}</td>
                  <td className="border px-4 py-2">{new Date(score.date_taken).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Set Password */}
        <div id="set-password" className="border rounded-lg p-4 shadow space-y-4">
          <h3 className="text-xl font-semibold">Set Common User Password</h3>
          <div className="flex items-center gap-4">
            <input className="border p-2 rounded" type="password" placeholder="New user password" value={commonUserPassword} onChange={(e) => setCommonUserPassword(e.target.value)} />
            <button onClick={handleSetUserPassword} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Set Password</button>
          </div>
        </div>

        {/* All Questions */}
        <div id="all-questions" className="border rounded-lg p-4 shadow space-y-4">
          <h3 className="text-xl font-semibold">All Quiz Questions</h3>
          <ul className="space-y-2 list-disc ml-6">
            {questions.map(q => (
              <li key={q.id}>
                <strong>Q{q.id}:</strong> {q.question} (Timer: {q.timer}s)
                <ul className="ml-4 list-disc">
                  <li>A: {q.option_a}</li>
                  <li>B: {q.option_b}</li>
                  <li>C: {q.option_c}</li>
                  <li>D: {q.option_d}</li>
                  <li><strong>Answer:</strong> {q.correct_answer}</li>
                </ul>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default AdminPage;
