// src/pages/AdminPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddQuestionForm from '../components/AddQuestionForm';
import EditQuestionForm from '../components/EditQuestionForm';
import UploadCSV from '../components/UploadCSV';

function AdminPage() {
  const [questions, setQuestions] = useState([]);
  const [scores, setScores] = useState([]);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [stats, setStats] = useState({});

  useEffect(() => {
    fetchQuestions();
    fetchScores();
    fetchStats();
  }, []);

  const fetchQuestions = async () => {
    const res = await axios.get('http://localhost:3000/quiz');
    setQuestions(res.data);
  };

  const fetchScores = async () => {
    const res = await axios.get('http://localhost:3000/scores');
    setScores(res.data);
  };

  const fetchStats = async () => {
    const res = await axios.get('http://localhost:3000/stats');
    setStats(res.data);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:3000/delete-quiz/${id}`);
    fetchQuestions();
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Admin Dashboard</h2>

      {/* Dashboard Stats */}
      <div className="grid grid-cols-3 gap-4 mb-6">
        <div className="p-4 bg-gray-100 rounded shadow">Total Questions: {stats.totalQuestions}</div>
        <div className="p-4 bg-gray-100 rounded shadow">Total Scores: {stats.totalScores}</div>
        <div className="p-4 bg-gray-100 rounded shadow">Recent Score: {stats.latestScore || 'N/A'}</div>
      </div>

      {/* Add Question */}
      <AddQuestionForm onAdded={fetchQuestions} />

      {/* Upload CSV */}
      <UploadCSV onUploadComplete={fetchQuestions} />

      {/* Questions Table */}
      <h3 className="text-xl mt-8 mb-2">Quiz Questions</h3>
      <table className="w-full table-auto border">
        <thead>
          <tr className="bg-gray-200">
            <th>ID</th>
            <th>Question</th>
            <th>Type</th>
            <th>Timer</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {questions.map((q) => (
            <tr key={q.id} className="text-center">
              <td>{q.id}</td>
              <td>{q.question}</td>
              <td>{q.type}</td>
              <td>{q.timer}s</td>
              <td>
                <button
                  className="bg-blue-500 text-white px-2 py-1 mr-2 rounded"
                  onClick={() => setSelectedQuestion(q)}
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white px-2 py-1 rounded"
                  onClick={() => handleDelete(q.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Form */}
      {selectedQuestion && (
        <EditQuestionForm question={selectedQuestion} onClose={() => setSelectedQuestion(null)} onUpdated={fetchQuestions} />
      )}

      {/* Score Table */}
      <h3 className="text-xl mt-8 mb-2">User Scores</h3>
      <table className="w-full table-auto border">
        <thead>
          <tr className="bg-gray-200">
            <th>User</th>
            <th>Score</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {scores.map((s, idx) => (
            <tr key={idx} className="text-center">
              <td>{s.username}</td>
              <td>{s.score}</td>
              <td>{new Date(s.date_taken).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPage;
