import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, Typography, Box, Paper } from '@mui/material';

function AdminLogin() {
  const [adminID, setAdminID] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    // Dummy logic, replace with real API call
    if (adminID === 'admin' && password === 'admin123') {
      localStorage.setItem('adminToken', 'validToken');
      navigate('/admin');
    } else {
      alert('‚ùå Invalid credentials');
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #f0f4ff, #e6f7ff)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <Paper
        elevation={6}
        sx={{
          p: 5,
          borderRadius: '20px',
          maxWidth: 400,
          width: '90%',
          textAlign: 'center',
          backgroundColor: '#fff',
        }}
      >
        <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#333', mb: 3 }}>
          üîê Admin Login
        </Typography>
        <TextField
          fullWidth
          label="Admin ID"
          variant="outlined"
          value={adminID}
          onChange={(e) => setAdminID(e.target.value)}
          sx={{ mb: 2 }}
        />
        <TextField
          fullWidth
          label="Password"
          type="password"
          variant="outlined"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          sx={{ mb: 3 }}
        />
        <Button
          Width
          variant="contained"
          onClick={handleLogin}
          sx={{
            backgroundColor: '#2f855a',
            py: 1.2,
            fontWeight: 'bold',
            fontSize: '16px',
            borderRadius: '30px',
            boxShadow: '0 6px 12px rgba(47, 133, 90, 0.3)',
            '&:hover': {
              backgroundColor: '#38a169',
            },
          }}
        >
           Login
        </Button>
      </Paper>
    </Box>
  );
}

export default AdminLogin;
