import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';

import AdminPage from './pages/AdminPage';
import UserPage from './pages/UserPage';
import AdminLogin from './components/AdminLogin';
import UserLogin from './components/UserLogin';
import UserDashboard from './components/UserDashboard';

function App() {
  const [adminToken, setAdminToken] = useState(localStorage.getItem('adminToken') || '');
  const [userId, setUserId] = useState(localStorage.getItem('userId') || '');

  const handleAdminLoginSuccess = (token) => {
    localStorage.setItem('adminToken', token);
    setAdminToken(token);
  };

  const handleUserLoginSuccess = (id) => {
    localStorage.setItem('userId', id);
    setUserId(id);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('userId');
    setAdminToken('');
    setUserId('');
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      handleLogout();
    }, 60 * 60 * 1000); // Auto logout after 1 hour
    return () => clearTimeout(timer);
  }, []);

  return (
    <Router>
      <div className="App">
        {/* Header */}
        <header className="App-header" style={{ textAlign: 'center', padding: '20px' }}>
          <h1>Quiz Application</h1>
        </header>

        {/* Navigation */}
        <nav style={{ textAlign: 'center', marginBottom: '20px' }}>
          {adminToken ? (
            <>
              <Link to="/admin" style={{ margin: '0 10px', fontSize: '18px' }}>Admin Panel</Link>
              <span>|</span>
              <button
                onClick={handleLogout}
                style={{
                  margin: '0 10px',
                  fontSize: '18px',
                  background: 'none',
                  border: 'none',
                  color: 'red',
                  cursor: 'pointer',
                }}
              >
                Logout
              </button>
            </>
          ) : (
            <Link to="/admin" style={{ margin: '0 10px', fontSize: '18px' }}>Admin Login</Link>
          )}
          <span>|</span>
          {userId ? (
            <Link to="/quiz" style={{ margin: '0 10px', fontSize: '18px' }}>Quiz Dashboard</Link>
          ) : (
            <Link to="/user-login" style={{ margin: '0 10px', fontSize: '18px' }}>User Login</Link>
          )}
        </nav>

        {/* Routes */}
        <Routes>
          <Route
            path="/admin"
            element={
              adminToken ? (
                <AdminPage token={adminToken} />
              ) : (
                <AdminLogin onLoginSuccess={handleAdminLoginSuccess} />
              )
            }
          />
          <Route
            path="/quiz"
            element={
              userId ? <UserDashboard userId={userId} /> : <Navigate to="/user-login" />
            }
          />
          <Route
            path="/user-login"
            element={
              <UserLogin onLogin={handleUserLoginSuccess} />
            }
          />
          <Route path="/" element={<Navigate to="/quiz" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
