import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserPage() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);

  // Fetch quiz questions when component mounts
  useEffect(() => {
    axios.get('http://localhost:3000/quiz') // Adjust URL if needed
      .then(response => {
        setQuestions(response.data);
      })
      .catch(error => {
        console.error('Error fetching quiz questions:', error);
      });
  }, []);

  // Handle selection of an answer
  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
  };

  // Handle moving to the next question
  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setSelectedAnswer(null); // Reset selected answer for the next question
      setCurrentQuestionIndex(currentQuestionIndex + 1); // Move to next question
    } else {
      alert('You have completed the quiz!');
    }
  };

  const currentQuestion = questions[currentQuestionIndex];

  if (!currentQuestion) {
    return <div>Loading questions...</div>; // Loading state
  }

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2>{currentQuestion.question}</h2>

      <div style={{ marginBottom: '20px' }}>
        <button 
          style={styles.optionButton} 
          onClick={() => handleAnswerSelect(currentQuestion.option_a)}
        >
          {currentQuestion.option_a}
        </button>
        <button 
          style={styles.optionButton} 
          onClick={() => handleAnswerSelect(currentQuestion.option_b)}
        >
          {currentQuestion.option_b}
        </button>
        <button 
          style={styles.optionButton} 
          onClick={() => handleAnswerSelect(currentQuestion.option_c)}
        >
          {currentQuestion.option_c}
        </button>
        <button 
          style={styles.optionButton} 
          onClick={() => handleAnswerSelect(currentQuestion.option_d)}
        >
          {currentQuestion.option_d}
        </button>
      </div>

      <div>
        <p>Selected answer: {selectedAnswer ? selectedAnswer : 'None selected'}</p>
      </div>

      <div>
        <button 
          style={styles.nextButton} 
          onClick={handleNextQuestion}
          disabled={!selectedAnswer} // Disable the Next button until an answer is selected
        >
          Next Question
        </button>
      </div>
    </div>
  );
}

const styles = {
  optionButton: {
    display: 'block',
    margin: '5px 0',
    padding: '10px',
    fontSize: '16px',
    backgroundColor: '#f0f0f0',
    border: '1px solid #ccc',
    cursor: 'pointer',
    width: '100%',
  },
  nextButton: {
    marginTop: '20px',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    width: '100%',
  }
};

export default UserPage;
