import React, { useState } from 'react';
import { Tabs, Tab, Box, Typography } from '@mui/material';
import AddQuestion from './AddQuestion';
import ViewScores from './ViewScores'; // Will create later

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function AdminDashboard() {
  const [tab, setTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
  };

  return (
    <Box sx={{ width: '100%' }}>
      <Typography variant="h4" sx={{ textAlign: 'center', mt: 2 }}>
        Admin Dashboard
      </Typography>

      <Tabs value={tab} onChange={handleTabChange} centered sx={{ mt: 2 }}>
        <Tab label="Add Question" />
        <Tab label="Delete Question" />
        <Tab label="User Scores" />
      </Tabs>

      <TabPanel value={tab} index={0}>
        <AddQuestion />
      </TabPanel>

      <TabPanel value={tab} index={1}>
        <p>(To be added) Delete functionality coming in next step.</p>
      </TabPanel>

      <TabPanel value={tab} index={2}>
        <ViewScores />
      </TabPanel>
    </Box>
  );
}

export default AdminDashboard;
