import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import axios from 'axios';

function SetPasswordForm() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSetPassword = async () => {
    if (!userId || !password) {
      return setMessage('⚠️ Both fields are required.');
    }

    try {
      const res = await axios.post('http://localhost:3000/set-user-password', {
        user_id: userId,
        password,
      });

      setMessage(res.data);
    } catch (err) {
      console.error('Error setting password:', err);
      setMessage('❌ Failed to set password.');
    }
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>Set User Password</Typography>
      <TextField
        label="User ID"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        sx={{ m: 1 }}
      />
      <TextField
        label="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        type="password"
        sx={{ m: 1 }}
      />
      <br />
      <Button variant="contained" onClick={handleSetPassword}>Submit</Button>
      {message && (
        <Typography sx={{ mt: 2 }} color="primary">
          {message}
        </Typography>
      )}
    </Box>
  );
}

export default SetPasswordForm;
