import React, { useState } from 'react';
import axios from 'axios';
import './SetPasswordForm.css'; // Assuming you have some CSS for styling

function SetPasswordForm({ backendUrl, setMessage }) {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSetPassword = async (e) => {
    e.preventDefault();
    if (!userId || !password) {
      setErrorMessage('Please enter both user ID and password.');
      return;
    }

    try {
      const response = await axios.post(`${backendUrl}/set-password`, {
        user_id: userId,
        password: password,
      });

      if (response.data.success) {
        setMessage('Password successfully updated.');
        setUserId('');
        setPassword('');
      } else {
        setErrorMessage('Failed to set password.');
      }
    } catch (error) {
      console.error('Error setting password:', error);
      setErrorMessage('Error setting password. Please try again later.');
    }
  };

  return (
    <div className="set-password-container">
      <h3>Set User Password</h3>
      <form onSubmit={handleSetPassword} className="password-form">
        <div className="input-group">
          <label htmlFor="userId">User ID</label>
          <input
            type="text"
            id="userId"
            name="userId"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            placeholder="Enter User ID"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter Password"
            required
          />
        </div>

        {errorMessage && <div className="error-message">{errorMessage}</div>}

        <button type="submit" className="btn-submit">
          Set Password
        </button>
      </form>
    </div>
  );
}

export default SetPasswordForm;
