import React, { useState, useEffect } from 'react';
import {
  TextField, Button, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, Paper, Typography
} from '@mui/material';

function AdminPanel() {
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState({
    question: '',
    option_a: '',
    option_b: '',
    option_c: '',
    option_d: '',
    correct_answer: '',
    timer: 30,
  });

  const [userScores, setUserScores] = useState([]);

  useEffect(() => {
    fetchQuestions();
    fetchScores();
  }, []);

  const fetchQuestions = () => {
    fetch('http://localhost:3000/quiz')
      .then(res => res.json())
      .then(data => setQuestions(data));
  };

  const fetchScores = () => {
    fetch('http://localhost:3000/user-scores')
      .then(res => res.json())
      .then(data => setUserScores(data));
  };

  const handleChange = (e) => {
    setNewQuestion({ ...newQuestion, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3000/insert-quiz-data', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newQuestion)
    }).then(res => {
      if (res.ok) {
        alert('Question added!');
        fetchQuestions();
        setNewQuestion({
          question: '',
          option_a: '',
          option_b: '',
          option_c: '',
          option_d: '',
          correct_answer: '',
          timer: 30,
        });
      }
    });
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:3000/delete-quiz-data/${id}`, {
      method: 'DELETE'
    }).then(() => {
      alert('Question deleted!');
      fetchQuestions();
    });
  };

  return (
    <div style={{ padding: 30 }}>
      <Typography variant="h4" gutterBottom>Admin Panel</Typography>

      <form onSubmit={handleSubmit} style={{ marginBottom: 40 }}>
        <Typography variant="h6">Add New Question</Typography>
        {['question', 'option_a', 'option_b', 'option_c', 'option_d', 'correct_answer'].map((field) => (
          <TextField
            key={field}
            name={field}
            label={field.replace(/_/g, ' ').toUpperCase()}
            value={newQuestion[field]}
            onChange={handleChange}
            fullWidth
            style={{ marginBottom: 10 }}
          />
        ))}
        <TextField
          name="timer"
          label="TIMER (seconds)"
          type="number"
          value={newQuestion.timer}
          onChange={handleChange}
          fullWidth
          style={{ marginBottom: 10 }}
        />
        <Button variant="contained" color="primary" type="submit">Add Question</Button>
      </form>

      <Typography variant="h6">Existing Questions</Typography>
      <TableContainer component={Paper} style={{ marginBottom: 40 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Question</TableCell>
              <TableCell>Options</TableCell>
              <TableCell>Correct</TableCell>
              <TableCell>Timer</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {questions.map(q => (
              <TableRow key={q.id}>
                <TableCell>{q.question}</TableCell>
                <TableCell>
                  A: {q.option_a}, B: {q.option_b},<br />
                  C: {q.option_c}, D: {q.option_d}
                </TableCell>
                <TableCell>{q.correct_answer}</TableCell>
                <TableCell>{q.timer}s</TableCell>
                <TableCell>
                  <Button color="secondary" onClick={() => handleDelete(q.id)}>Delete</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Typography variant="h6">User Scores</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>User</TableCell>
              <TableCell>Score</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {userScores.map((score, index) => (
              <TableRow key={index}>
                <TableCell>{score.username || score.user_name || 'Unknown'}</TableCell>
                <TableCell>{score.score}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}

export default AdminPanel;
