import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import AdminPage from './pages/AdminPage';
import UserPage from './pages/UserPage';
import AdminLogin from './components/AdminLogin';
import UserLogin from './components/UserLogin';
import HomePage from './components/HomePage';

function App() {
  const [adminToken, setAdminToken] = useState(localStorage.getItem('adminToken') || '');
  const [userLoggedIn, setUserLoggedIn] = useState(localStorage.getItem('userLoggedIn') === 'true');

  const handleAdminLogin = (token) => {
    localStorage.setItem('adminToken', token);
    setAdminToken(token);
  };

  const handleUserLogin = () => {
    localStorage.setItem('userLoggedIn', 'true');
    setUserLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('userLoggedIn');
    setAdminToken('');
    setUserLoggedIn(false);
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      handleLogout();
    }, 60 * 60 * 1000); // 1 hour logout

    return () => clearTimeout(timer);
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/admin-login" element={<AdminLogin onLoginSuccess={handleAdminLogin} />} />
        <Route path="/user-login" element={<UserLogin onLoginSuccess={handleUserLogin} />} />
        <Route
          path="/admin"
          element={adminToken ? <AdminPage token={adminToken} onLogout={handleLogout} /> : <Navigate to="/admin-login" />}
        />
        <Route
          path="/quiz"
          element={userLoggedIn ? <UserPage /> : <Navigate to="/user-login" />}
        />
      </Routes>
    </Router>
  );
}

export default App;
