import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import AdminPage from './pages/AdminPage';   // Admin Panel (after login)
import UserPage from './pages/UserPage';     // User Quiz Page
import AdminLogin from './components/AdminLogin'; // Admin Login Component

function App() {
  const [adminToken, setAdminToken] = useState(localStorage.getItem('adminToken') || '');

  const handleLoginSuccess = (token) => {
    localStorage.setItem('adminToken', token);
    setAdminToken(token);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    setAdminToken('');
  };

  useEffect(() => {
    // Optional: auto-logout after 1 hour
    const timer = setTimeout(() => {
      handleLogout();
    }, 60 * 60 * 1000); // 1 hour

    return () => clearTimeout(timer);
  }, []);

  return (
    <Router>
      <div className="App">
        {/* Header */}
        <header className="App-header" style={{ textAlign: 'center', padding: '20px' }}>
          <h1>Quiz Application</h1>
        </header>

        {/* Navigation */}
        <nav style={{ textAlign: 'center', marginBottom: '20px' }}>
          {adminToken ? (
            <>
              <Link to="/admin" style={{ margin: '0 10px', fontSize: '18px' }}>
                Admin Panel
              </Link>
              <span>|</span>
              <button onClick={handleLogout} style={{ margin: '0 10px', fontSize: '18px', color: 'red' }}>
                Logout
              </button>
            </>
          ) : (
            <Link to="/admin" style={{ margin: '0 10px', fontSize: '18px' }}>
              Admin Login
            </Link>
          )}
          <span>|</span>
          <Link to="/quiz" style={{ margin: '0 10px', fontSize: '18px' }}>
            Take Quiz
          </Link>
        </nav>

        {/* Routes */}
        <Routes>
          <Route
            path="/admin"
            element={
              adminToken ? (
                <AdminPage token={adminToken} />
              ) : (
                <AdminLogin onLoginSuccess={handleLoginSuccess} />
              )
            }
          />
          <Route path="/quiz" element={<UserPage />} />
          <Route path="/" element={<Navigate to="/quiz" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
