import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserPage = () => {
  const [score, setScore] = useState(null);
  const [loading, setLoading] = useState(true);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const runQuiz = async () => {
      try {
        const result = await axios.get('http://localhost:3000/run-quiz');
        const output = result.data;

        // üîç Extract score from stdout (assuming "Final Score: 3")
        const match = output.match(/Final Score:\s*(\d+)/);
        const finalScore = match ? parseInt(match[1]) : 0;

        setScore(finalScore);

        // ‚úÖ Submit score to backend
        await axios.post('http://localhost:3000/submit-score', {
          user_id: userId,
          score: finalScore
        });
      } catch (err) {
        console.error('Error running quiz or saving score:', err);
      } finally {
        setLoading(false);
      }
    };

    runQuiz();
  }, [userId]);

  return (
    <div className="user-quiz">
      {loading ? (
        <p>Running quiz...</p>
      ) : (
        <div>
          <h2>Your Score: {score}</h2>
          <p>Thank you for participating!</p>
        </div>
      )}
    </div>
  );
};

export default UserPage;
