import React, { useState, useEffect } from 'react';
import {
  Tabs, Tab, Box, Typography, CircularProgress, Button,
  Card, CardContent, AppBar, Toolbar, IconButton, Container, Grid
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import LogoutIcon from '@mui/icons-material/Logout';
import AddQuestion from './AddQuestion';
import ViewScores from './ViewScores';
import { useNavigate } from 'react-router-dom';

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && (
        <Box sx={{ py: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function AdminDashboard() {
  const [tab, setTab] = useState(0);
  const [quizData, setQuizData] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    navigate('/admin'); // redirect to login
  };

  useEffect(() => {
    fetch('http://localhost:3000/quiz')
      .then((res) => res.json())
      .then((data) => {
        setQuizData(data);
        setLoading(false);
      })
      .catch((error) => console.error('Error fetching quiz data:', error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:3000/delete-quiz/${id}`, {
      method: 'DELETE',
    })
      .then(() => {
        setQuizData((prev) => prev.filter((q) => q.id !== id));
        alert('Question deleted successfully');
      })
      .catch((error) => console.error('Error deleting quiz question:', error));
  };

  return (
    <>
      {/* Top AppBar with Logout */}
      <AppBar position="static" color="primary" sx={{ mb: 2 }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Typography variant="h6" component="div">
            Admin Dashboard
          </Typography>
          <Button
            color="inherit"
            startIcon={<LogoutIcon />}
            onClick={handleLogout}
          >
            Logout
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="md" sx={{ backgroundColor: '#fafafa', p: 3, borderRadius: 2, boxShadow: 2 }}>
        <Tabs
          value={tab}
          onChange={handleTabChange}
          centered
          textColor="primary"
          indicatorColor="primary"
          sx={{ mb: 3 }}
        >
          <Tab label="Add Question" />
          <Tab label="Delete Question" />
          <Tab label="User Scores" />
        </Tabs>

        <TabPanel value={tab} index={0}>
          <AddQuestion />
        </TabPanel>

        <TabPanel value={tab} index={1}>
          <Typography variant="h6" gutterBottom>
            Delete Questions
          </Typography>

          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
              <CircularProgress />
            </Box>
          ) : quizData.length === 0 ? (
            <Typography>No questions available.</Typography>
          ) : (
            <Grid container spacing={2}>
              {quizData.map((question) => (
                <Grid item xs={12} key={question.id}>
                  <Card elevation={3} sx={{ borderRadius: 2, backgroundColor: '#fff' }}>
                    <CardContent>
                      <Typography variant="subtitle1" sx={{ fontWeight: 500 }}>
                        Q: {question.question}
                      </Typography>
                      <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
                        <Button
                          variant="contained"
                          color="error"
                          startIcon={<DeleteIcon />}
                          onClick={() => handleDelete(question.id)}
                        >
                          Delete
                        </Button>
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </TabPanel>

        <TabPanel value={tab} index={2}>
          <ViewScores />
        </TabPanel>
      </Container>
    </>
  );
}

export default AdminDashboard;
