import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';

function UserDashboard() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [loading, setLoading] = useState(true);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [hasStarted, setHasStarted] = useState(false);
  const [timeRemaining, setTimeRemaining] = useState({});

  const timerRef = useRef(null);
  const userId = localStorage.getItem('userId');

  // Fetch questions
  useEffect(() => {
    axios.get('http://localhost:3000/quiz')
      .then(res => {
        setQuestions(res.data);
        const initialTimers = {};
        res.data.forEach(q => {
          initialTimers[q.id] = q.timer || 30;
        });
        setTimeRemaining(initialTimers);
        setLoading(false);
      })
      .catch(err => {
        console.error('❌ Error fetching quiz:', err);
        setLoading(false);
      });
  }, []);

  // Timer logic on question change & start
  useEffect(() => {
    if (!hasStarted || submitted || questions.length === 0) return;

    // Clear any existing timer
    if (timerRef.current) clearInterval(timerRef.current);

    const currentQuestion = questions[currentIndex];
    if (!currentQuestion) return; // safety check

    const qid = currentQuestion.id;

    timerRef.current = setInterval(() => {
      setTimeRemaining(prev => {
        const updated = { ...prev };
        if (updated[qid] <= 1) {
          clearInterval(timerRef.current);
          updated[qid] = 0;
          setTimeout(() => handleAutoNext(), 500);
        } else {
          updated[qid] -= 1;
        }
        return updated;
      });
    }, 1000);

    // Cleanup on unmount or question change
    return () => clearInterval(timerRef.current);

  }, [currentIndex, hasStarted, submitted, questions]);

  const handleOptionChange = (questionId, selectedOption) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: selectedOption,
    }));
  };

  const handleAutoNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(prev => prev + 1);
    } else {
      handleSubmit();
    }
  };

  const handleSubmit = async () => {
    if (submitted) return;

    let calculatedScore = 0;
    const responses = [];

    questions.forEach((q) => {
      const selected = answers[q.id];
      const isCorrect = selected === q.correct_answer;

      if (selected) {
        responses.push({
          question_id: q.id,
          selected_answer: selected,
          is_correct: isCorrect,
        });

        if (isCorrect) calculatedScore += 1;
      }
    });

    setScore(calculatedScore);
    setSubmitted(true);
    clearInterval(timerRef.current);

    try {
      await axios.post('http://localhost:3000/submit-quiz', {
        user_id: userId,
        score: calculatedScore,
        responses,
      });
      console.log('✅ Quiz submitted');
    } catch (err) {
      console.error('❌ Failed to submit quiz:', err);
    }
  };

  // Pause current timer before navigation
  const pauseCurrentTimer = () => {
    if (timerRef.current) clearInterval(timerRef.current);
  };

  // Navigate to next question & pause current timer
  const goNext = () => {
    pauseCurrentTimer();
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(prev => prev + 1);
    }
  };

  // Navigate to previous question & pause current timer
  const goPrev = () => {
    pauseCurrentTimer();
    if (currentIndex > 0) {
      setCurrentIndex(prev => prev - 1);
    }
  };

  if (loading) return <p>Loading quiz questions...</p>;

  if (submitted) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>Thank you for submitting the quiz!</h2>
        <p>Your final score: <strong>{score}</strong></p>
      </div>
    );
  }

  if (!hasStarted) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>Quiz Rules</h2>
        <ul style={{ lineHeight: '1.8' }}>
          <li>You must answer all questions to complete the quiz.</li>
          <li>Each question has only one correct answer.</li>
          <li>Each question has a time limit.</li>
          <li>No negative marking.</li>
          <li>Your final score will be shown after submission.</li>
        </ul>
        <button
          onClick={() => setHasStarted(true)}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          Start Quiz
        </button>
      </div>
    );
  }

  // Safe check current question to avoid runtime errors
  const currentQuestion = questions[currentIndex] || null;
  const timer = currentQuestion ? (timeRemaining[currentQuestion.id] || 0) : 0;

  if (!currentQuestion) {
    return <p>No question found.</p>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h2>Question {currentIndex + 1} of {questions.length}</h2>
        <div style={{
          fontSize: '18px',
          fontWeight: 'bold',
          color: timer <= 5 ? 'red' : 'black'
        }}>
          ⏰ Time left: {timer}s
        </div>
      </div>

      <div style={{
        marginBottom: '20px',
        border: '1px solid #ccc',
        padding: '15px',
        borderRadius: '8px'
      }}>
        <h3 dangerouslySetInnerHTML={{ __html: currentQuestion.question }} />
        {['A', 'B', 'C', 'D'].map(opt => (
          <label key={opt} style={{ display: 'block', margin: '5px 0' }}>
            <input
              type="radio"
              name={`question-${currentQuestion.id}`}
              value={opt}
              checked={answers[currentQuestion.id] === opt}
              onChange={() => handleOptionChange(currentQuestion.id, opt)}
            />
            {opt === 'A' && ` ${currentQuestion.option_a}`}
            {opt === 'B' && ` ${currentQuestion.option_b}`}
            {opt === 'C' && ` ${currentQuestion.option_c}`}
            {opt === 'D' && ` ${currentQuestion.option_d}`}
          </label>
        ))}
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <button onClick={goPrev} disabled={currentIndex === 0}>
          Previous
        </button>

        {currentIndex === questions.length - 1 ? (
          <button
            onClick={handleSubmit}
            style={{ backgroundColor: '#4CAF50', color: 'white' }}>
            Submit Quiz
          </button>
        ) : (
          <button onClick={goNext}>
            Next
          </button>
        )}
      </div>
    </div>
  );
}

export default UserDashboard;
